import React from 'react'
import classNames from 'classnames'

import useFetch from 'hooks/useFetch'

const AddToFavourites = ({isFavorited, favoritesCount, articleSlug }) => {
    const apiUrl = `/articles/${articleSlug}/favorite`
    const [{response}, doFetch] = useFetch(apiUrl)
    const isFavoritedWithResponse = response ? response.article.favorited : isFavorited 
    const isFavourteCountWithResponse =  response ? response.article.favoritesCount : favoritesCount

    const buttonClasses = classNames({
        btn: true,
       'btn-sm': true,
        'btn-primary': isFavoritedWithResponse,
        'btn-outline-primary': !isFavoritedWithResponse
    })
s
    const handleLike = (e) => {
        e.preventDefault()
        doFetch({
            method: isFavoritedWithResponse ? 'delete' : 'post'
        })
    }
    
    return (
        <button className={buttonClasses} onClick={handleLike}>
            <i className='ion-heart'></i>
            <span>&nbsp;{favoritesCount}</span>
        </button>
    )
}

export default AddToFavourites
